<script>
import "swiper/dist/css/swiper.css";
import moment from "moment";

export default {
  data() {
    return {
      event_type: "party",
      event_types: ["presentation", "workshop", "hackathon", "party"],
      event_search: "",
    };
  },
  methods: {
      changeMsg() {
          this.$emit("changeMsg", 'proposal');
        }
  },
  mounted: function() {
  
    var counter = 0;

       setInterval(
      function() {
        if (counter == this.event_types.length - 1) {
          counter = 0;
        } else {
          counter = counter + 1;
        }
        this.event_type = this.event_types[counter];
      }.bind(this),
      2000
    );
 
  },
  filters: {
    formatDate: function(value) {
      return moment(String(value)).format("DD/MM/YY");
    }
  },
  computed: {
    filteredList() {
      return this.data.filter(event => {
        return (
          event.title.toLowerCase().includes(this.search.toLowerCase()) ||
          event.location && event.location.toLowerCase().includes(this.search.toLowerCase()) ||
          event.date && event.date.toLowerCase().includes(this.search.toLowerCase()) ||
          event.type && event.type.toLowerCase().includes(this.search.toLowerCase())
        );
      });
    }
  },
  props: ["data", "search"],
   watch: {
    search(newVal) {

      return newVal;
    }
  }
};
</script>
